# shellcheck shell=bash

module_system_prep() {
  log_section "System prep"
  apt-get update
  DEBIAN_FRONTEND=noninteractive apt-get upgrade -y
  DEBIAN_FRONTEND=noninteractive apt-get install -y \
    software-properties-common curl wget git unzip ca-certificates apt-transport-https \
    build-essential pkg-config jq gnupg2 lsb-release ufw fail2ban htop acl
}

register_step module_system_prep

# shellcheck shell=bash

module_system_config() {
  log_section "Swap ${SWAP_SIZE}"
  if [ ! -f /swapfile ]; then
    fallocate -l "${SWAP_SIZE}" /swapfile || dd if=/dev/zero of=/swapfile bs=1M count=2048
    chmod 600 /swapfile
    mkswap /swapfile
    swapon /swapfile
    echo '/swapfile none swap sw 0 0' >> /etc/fstab
  fi

  log_section "Sysctl and limits"
  install_template "etc/sysctl/99-hardening.conf" "/etc/sysctl.d/99-hardening.conf"
  sysctl --system
  install_template "etc/security/99-web.conf" "/etc/security/limits.d/99-web.conf"
}

register_step module_system_config

# shellcheck shell=bash

module_security_hardening() {
  log_section "UFW + Fail2ban"
  ufw --force enable
  ufw default deny incoming
  ufw default allow outgoing
  ufw allow 22/tcp
  ufw allow 80/tcp
  ufw allow 443/tcp
  ufw allow 5173/tcp comment 'Vite dev server'
  ufw allow 8025/tcp comment 'Mailpit web UI'
  ufw allow 8080/tcp comment 'Adminer'
  install_template "etc/fail2ban/jail.local" "/etc/fail2ban/jail.local"
  systemctl enable --now fail2ban

  log_section "SSH hardening"
  local conf=/etc/ssh/sshd_config
  declare -A kv=(
    ["PasswordAuthentication"]="no"
    ["PermitRootLogin"]="no"
    ["PubkeyAuthentication"]="yes"
    ["X11Forwarding"]="no"
    ["ClientAliveInterval"]="300"
    ["ClientAliveCountMax"]="2"
    ["MaxAuthTries"]="3"
    ["MaxSessions"]="10"
  )
  for k in "${!kv[@]}"; do
    if grep -qE "^\\s*${k}\\b" "$conf"; then
      sed -i "s#^\\s*${k}.*#${k} ${kv[$k]}#" "$conf"
    else
      echo "${k} ${kv[$k]}" >>"$conf"
    fi
  done
  systemctl restart ssh || systemctl restart sshd || true
}

register_step module_security_hardening

# shellcheck shell=bash

module_nginx_setup() {
  log_section "Nginx install"
  DEBIAN_FRONTEND=noninteractive apt-get install -y nginx

  mkdir -p /var/www
  chown -R ubuntu:www-data /var/www
  chmod 2775 /var/www

  install_template "etc/profile.d/umask-002.sh" "/etc/profile.d/umask-002.sh"
  install_template "etc/nginx/gzip.conf" "/etc/nginx/conf.d/gzip.conf"
  install_template "etc/nginx/sites-available_default" "/etc/nginx/sites-available/default"

  ensure_dir /var/www/placeholder
  render_template "var/www/placeholder_index.html" > /var/www/placeholder/index.html
  chmod 0644 /var/www/placeholder/index.html
  chown -R www-data:www-data /var/www/placeholder
}

register_step module_nginx_setup

# shellcheck shell=bash

module_php_stack() {
  log_section "PHP ${PHP_VERSION}"
  add-apt-repository -y ppa:ondrej/php
  apt-get update
  DEBIAN_FRONTEND=noninteractive apt-get install -y \
    php${PHP_VERSION}-fpm php${PHP_VERSION}-cli php${PHP_VERSION}-common \
    php${PHP_VERSION}-pgsql php${PHP_VERSION}-sqlite3 php${PHP_VERSION}-curl \
    php${PHP_VERSION}-mbstring php${PHP_VERSION}-xml php${PHP_VERSION}-zip \
    php${PHP_VERSION}-gd php${PHP_VERSION}-bcmath php${PHP_VERSION}-intl \
    php${PHP_VERSION}-readline php${PHP_VERSION}-imagick php${PHP_VERSION}-opcache \
    php${PHP_VERSION}-redis php${PHP_VERSION}-pcov

  install_template "etc/php/fpm_pool_www.conf" "/etc/php/${PHP_VERSION}/fpm/pool.d/www.conf"
  sed -i "s/__PHP_VERSION__/${PHP_VERSION}/g" "/etc/php/${PHP_VERSION}/fpm/pool.d/www.conf"

  install_template "etc/php/fpm_conf_zz-custom.ini" "/etc/php/${PHP_VERSION}/fpm/conf.d/zz-custom.ini"

  log_section "Configuring inotify watchers for development tools"
  echo "fs.inotify.max_user_watches=524288" >> /etc/sysctl.conf
  echo "fs.inotify.max_user_instances=512" >> /etc/sysctl.conf
  sysctl -p

  sed -i 's/^memory_limit = .*/memory_limit = 256M/' /etc/php/${PHP_VERSION}/cli/php.ini
  sed -i 's/^upload_max_filesize = .*/upload_max_filesize = 64M/' /etc/php/${PHP_VERSION}/cli/php.ini
  sed -i 's/^post_max_size = .*/post_max_size = 64M/' /etc/php/${PHP_VERSION}/cli/php.ini
  sed -i 's/^max_execution_time = .*/max_execution_time = 300/' /etc/php/${PHP_VERSION}/cli/php.ini

  systemctl enable --now php${PHP_VERSION}-fpm
  ln -sf /run/php/php${PHP_VERSION}-fpm.sock /run/php/php-fpm.sock

  log_section "Composer + Laravel installer"
  export HOME=/root COMPOSER_HOME=/root/.composer
  EXPECTED_CHECKSUM="$(curl -fsSL https://composer.github.io/installer.sig)"
  php -r "copy('https://getcomposer.org/installer','composer-setup.php');"
  ACTUAL_CHECKSUM="$(php -r 'echo hash_file("sha384","composer-setup.php");')"
  if [ "$EXPECTED_CHECKSUM" != "$ACTUAL_CHECKSUM" ]; then
    echo "Composer installer checksum mismatch"; rm composer-setup.php; exit 1
  fi
  php composer-setup.php --install-dir=/usr/local/bin --filename=composer
  rm composer-setup.php

  sudo -u ubuntu -H /usr/local/bin/composer global require laravel/installer

  install_template "etc/profile.d/composer-path.sh" "/etc/profile.d/composer-path.sh"

  if [ -x /home/ubuntu/.config/composer/vendor/bin/laravel ]; then
    ln -sf /home/ubuntu/.config/composer/vendor/bin/laravel /usr/local/bin/laravel
  elif [ -x /home/ubuntu/.composer/vendor/bin/laravel ]; then
    ln -sf /home/ubuntu/.composer/vendor/bin/laravel /usr/local/bin/laravel
  fi
}

register_step module_php_stack

